mapper.select:
select article_id id,title,content,article_like,username,image from article a
                                                                        left join user u on a.user_id = u.user_id
                                                                        where u.user_id=#{id};

select article_id id,title,content,a.user_id ,username,image,article_like,publish_time  from article a
                                                                                            left join user u on a.user_id = u.user_id
                                                                                            where a.article_id=#{id};
