mapper.update:
update article
title=#{title},
content = #{content},
image=#{image},
article_like = #{articleLike}
,update_time= now() where article_id=#{id};

mapper.select:
select article_id id,title,content,a.user_id ,username,image,article_like,publish_time,a.update_time  from article a
                                                                                            left join user u on a.user_id = u.user_id
                                                                                            where a.article_id=#{id};

select article_id id,title,content,user_id,image,publish_time,update_time,article_like from article


        and article_id = #{id}


        and title like concat('%',#{title},'%')


        and content like concat('%',#{content},'%')


        and user_id = #{userId}
